import React, {useState} from 'react';
import '../css/AttendanceReportCreatePage.css'; // CSS 파일 import
import '../css/Base.css';

const AttendanceReportCreatePage = () => {
    const [reportData, setReportData] = useState({
        date: '2024-11-12',
        subject: '웹프로그래밍',
        classCode: 'WEB001',
        students: [
            {id: 20201234, name: '김철수', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20205678, name: '이영희', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20209012, name: '박민수', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20203456, name: '정지훈', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20207890, name: '최수진', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20202345, name: '강동원', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20206789, name: '윤서연', status: '출석', image: '/api/placeholder/120/150'},
            {id: 20201111, name: '한지민', status: '출석', image: '/api/placeholder/120/150'},
            // 8x4 그리드를 위해 32명의 더미 데이터 추가
            ...Array(24).fill(null).map((_, index) => ({
                id: 20200001 + index,
                name: `학생${index + 9}`,
                status: '출석',
                image: '/api/placeholder/120/150'
            }))
        ]
    });

    const [classCodeInput, setClassCodeInput] = useState('');

    const handleStatusChange = (studentId, newStatus) => {
        setReportData(prev => ({
            ...prev,
            students: prev.students.map(student =>
                student.id === studentId
                    ? {...student, status: newStatus}
                    : student
            )
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('제출된 보고서:', reportData);
    };

    return (
        <div className="min-h-screen bg-gray-50 p-8">
            <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg p-6">
                {/* 헤더 섹션 */}
                <div className="mb-8 grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">날짜</label>
                        <input
                            type="date"
                            value={reportData.date}
                            onChange={e => setReportData({...reportData, date: e.target.value})}
                            className="w-full rounded-md border-gray-300 shadow-sm p-2 border"
                            required
                        />
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">과목명</label>
                        <input
                            type="text"
                            value={reportData.subject}
                            onChange={e => setReportData({...reportData, subject: e.target.value})}
                            className="w-full rounded-md border-gray-300 shadow-sm p-2 border"
                            required
                        />
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">수업 코드</label>
                        <div className="flex gap-2">
                            <input
                                type="text"
                                value={classCodeInput}
                                onChange={e => setClassCodeInput(e.target.value)}
                                placeholder="출석 코드 입력"
                                className="flex-1 rounded-md border-gray-300 shadow-sm p-2 border"
                            />
                            <button
                                onClick={() => console.log('출석 코드 확인')}
                                className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                            >
                                확인
                            </button>
                        </div>
                    </div>
                </div>

                {/* 학생 그리드 */}
                <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-8 gap-4">
                    {reportData.students.map(student => (
                        <div key={student.id}
                             className="flex flex-col items-center p-2 border rounded-lg hover:shadow-md transition-shadow">
                            <img
                                src={student.image}
                                alt={student.name}
                                className="w-24 h-32 object-cover mb-2 rounded"
                            />
                            <div className="text-center">
                                <div className="text-sm font-medium text-gray-900">{student.id}</div>
                                <div className="text-sm text-gray-600">{student.name}</div>
                            </div>
                            <select
                                value={student.status}
                                onChange={e => handleStatusChange(student.id, e.target.value)}
                                className="mt-2 w-full text-sm rounded border-gray-300 shadow-sm"
                            >
                                <option value="출석">출석</option>
                                <option value="지각">지각</option>
                                <option value="결석">결석</option>
                                <option value="조퇴">조퇴</option>
                            </select>
                        </div>
                    ))}
                </div>

                {/* 제출 버튼 */}
                <div className="flex justify-end mt-6">
                    <button
                        type="submit"
                        onClick={handleSubmit}
                        className="px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                    >
                        출석부 저장
                    </button>
                </div>
            </div>
        </div>
    );
}
export default AttendanceReportCreatePage;
