import React from 'react';
import '../css/AttendanceReportCreatePage.css'; // CSS 파일 import
import '../css/Base.css';

const AttendanceReportCreatePage = () => {
    const [reportData, setReportData] = useState({
        date: '2024-11-12',
        subject: '웹프로그래밍',
        class: 'A반',
        week: '1',
        students: [
            { id: 20201234, name: '김철수', status: '출석' },
            { id: 20205678, name: '이영희', status: '출석' },
            { id: 20209012, name: '박민수', status: '출석' },
        ]
    });

    const handleStatusChange = (studentId, newStatus) => {
        setReportData(prev => ({
            ...prev,
            students: prev.students.map(student =>
                student.id === studentId
                    ? {...student, status: newStatus}
                    : student
            )
        }));
    };
    return (
        <div className="main-container">
            <div className="content-container">
                <h1 className="title">출석 보고서 작성</h1>
                <div className="AttendanceReportCreatePage-content">
                    <div className="form-header">
                        <input
                            type="date"
                            value={reportData.date}
                            onChange={e => setReportData({...reportData, date: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm p-2 border"
                            required
                        />
                    </div>
                </div>
            </div>
        </div>

    );
};

export default AttendanceReportCreatePage;
